package pillowfighttimer

import (
	"log"
	"os/exec"
	"time"
)

func sayIt(thingToSay string) {
	cmd := exec.Command("say", thingToSay)

	err := cmd.Run()

	if err != nil {
		log.Fatal(err)
	}
}

func main() {
	// thingToSay := "the pillow fight is over now"
	thingToSay := "now it is time to be done Tozer"
	// thingToSay := "chad is a hippo"
	// thingToSay := "chad is a "

	time.Sleep(time.Second * 10)

	for i := 0; i < 300; i++ {
		sayIt(thingToSay)
		time.Sleep(time.Second * 5)
	}
}
